var Util = require('./util.js');

/**
 * Android and iOS compatible wakelock implementation.
 *
 * Refactored thanks to dkovalev@.
 */
function AndroidWakeLock() {
  var video = document.createElement('video');

  video.addEventListener('ended', function() {
    video.play();
  });

  this.request = function() {
    if (video.paused) {
      // Base64 version of a mp4 video for the wakelock, exchanged
      // videos_src/no-sleep-60s.webm data with the one from
      // https://github.com/googlevr/webvr-polyfill/blob/v0.9.34/src/wakelock.js
      // Since the old one caused a weird issue on some mobile devices
      // (like the Samsung Galaxy S6). These devices stop playing mp4
      // videos, which are used as textures ('a-video' or
      // 'a-videosphere') as soon as the VR mode is active. After the
      // issue appears it is not possible to play any mp4 videos
      // anymore until the device is restarted.
      video.src = Util.base64('video/webm', '');
      video.play();
    }
  };

  this.release = function() {
    video.pause();
    video.src = '';
  };
}

function iOSWakeLock() {
  var timer = null;

  this.request = function() {
    if (!timer) {
      timer = setInterval(function() {
        window.location = window.location;
        setTimeout(window.stop, 0);
      }, 30000);
    }
  }

  this.release = function() {
    if (timer) {
      clearInterval(timer);
      timer = null;
    }
  }
}


function getWakeLock() {
  var userAgent = navigator.userAgent || navigator.vendor || window.opera;
  if (userAgent.match(/iPhone/i) || userAgent.match(/iPod/i)) {
    return iOSWakeLock;
  } else {
    return AndroidWakeLock;
  }
}

module.exports = getWakeLock();
